export var componentList = {};
export function getComponent(component, name) {
  if (component) {
    var localComponents = component.constructor.components;
    var visualizerComponents = component.$v.components;
    if (localComponents && name in localComponents) return localComponents[name];
    if (visualizerComponents && name in visualizerComponents) return visualizerComponents[name];
  }

  if (name in componentList) return componentList[name];
  throw new Error("Cannot find element: ".concat(name));
}
