import { Behavior } from "./behavior";
import Drag from "./drag";
import Tooltip from "./tooltip";
import Zoom from "./zoom";
var behaviors = {
  "drag": Drag,
  "zoom": Zoom,
  "tooltip": Tooltip
};
var defaultKeys = new Set(Object.keys(behaviors));
export function getBehavior(name) {
  return behaviors[name];
}
export function registerBehavior(name, behavior) {
  if (defaultKeys.has(name)) {
    throw Error("Cannot overwrite default behavior ".concat(name));
  }

  if (!(behavior instanceof Behavior)) {
    throw Error("Cannot register behavior: the object is not a behavior class");
  }

  behaviors[name] = behavior;
}
