import { NAME } from "../tokens";
import { consumeExpr } from "./prop";
var EXT_COMMAND = "@(\\w[\\w_0-9]*\\b)";
export function parseExtCommand(p) {
  p.skipSpaces();
  var match = p.test(EXT_COMMAND);
  if (!match) return null;

  switch (match[1]) {
    case "load":
      p.expect("@load");
      p.skipSpaces();
      var name = p.expect(NAME)[0];
      p.skipSpaces();
      var remaining = consumeExpr(p);
      return {
        type: "load",
        name: name,
        payload: remaining
      };

    default:
      return null;
  }
}
